const loraId = 10009;
const fuelLimit = 150;

const rng = (min, max) => (Math.random() * (max - min + 1) + min).toFixed(2);
const rngB = () => Math.random() > 0.5;
const nroMM = () => {
    const nro = global.get("nroMM") || 1;
    global.set("nroMM", nro + 1);
    return nro;
}
const trunc = (str) => parseFloat(str.charAt(0))

const generarPayloadInt = () => `<${loraId}>voltage=${rng(210, 230)}&temperature=${rng(5, 30)}`;
const generarPayloadExt = () => `<${10000 + loraId}>current=${rng(0.5, 10)}&raindrops=${rngB()}&gas=${rng(0, fuelLimit)}/${fuelLimit}&lat=-34.57475&lng=58.43552&alt=15`;

/* 
    nro_mm: 2,
    clasificacion: "reservado",
    precedencia: "rutina",
    cifrado: true,
    destino: "Puesto Comando",
    origen: "Centro de Mensajes",
    evento: "entregar",
*/
const generarMM = () => {
    const clasificacion = trunc(rng(1, 4));             // 1: publico, 2: reservado, 3: confidencial, 4: secreto
    const numeroMsj = nroMM();                              
    const precedencia = trunc(rng(1, 4));               // 1: rutina, 2: prioridad, 3: inmediato, 4: flash
    const cifrado = clasificacion >= 3 ? + rngB() : 0;      
    const indexOrigen = trunc(rng(1, 5));               // 1: Red Cdo Op, 2: Red Mat Pers, 3: Red Icia, 4: Red Cdo, 5: Red Op
    const indexDestino = indexOrigen % 5 + 1;           // 1: Red Cdo Op, 2: Red Mat Pers, 3: Red Icia, 4: Red Cdo, 5: Red Op
    const evento = trunc(rng(1, 4));                    // 1: generado, 2: recibido_estafeta, 3: entregado_estafeta, 4: transmitido 

    const nro = numeroMsj % 65536;

    const nroArr = [0, 0, 0];
    nroArr[0] = (nro - nro % 256) / 256;
    nroArr[1] = nro % 256;
    nroArr[2] = 0;

    const dataArr = [0, 0, 0];
    dataArr[0] += (clasificacion - 1) * 2**0;    // bits 0 y 1
    dataArr[0] += (precedencia - 1) * 2**2;      // bits 2 y 3
    dataArr[0] += (indexOrigen - 1) * 2**4;      // bits 4, 5 y 6
    dataArr[1] += (indexDestino - 1) * 2**0;     // bits 0, 1 y 2
    dataArr[1] += (evento - 1) * 2**3;           // bits 3 y 4
    dataArr[1] += cifrado * 2**5;                // bit 5


    // debug (no es outputteable a mqtt)
    msg.metadata = {
        clasificacion,
        numeroMsj,
        precedencia,
        cifrado,
        indexOrigen,
        indexDestino, 
        evento,
        bytesNumeroMsj: `${nroArr[0]} ${nroArr[1]} ${nroArr[2]}`,
        bytesData: `${dataArr[0]} ${dataArr[1]} ${dataArr[2]}`,
    };

    return Buffer.from(`<${loraId}>nro=${String.fromCharCode(...nroArr)}&data=${String.fromCharCode(...dataArr)}`);
}

if (msg.payload === "nodo interior") {
    msg.payload = generarPayloadInt();
} else if (msg.payload === "nodo exterior") {
    msg.payload = generarPayloadExt();
} else if (msg.payload === "mensaje militar") {
    msg.payload = generarMM();
}

return msg;

const isMM = typeof msg.payload !== "string";
let idFacility;
let header = "";
if (isMM) {
    const buffer = msg.payload.subarray(0, 7);
    header = buffer.toString('utf-8');
} else {
    header = msg.payload.substring(0, 7);
}
// p. ej.: header = "<10009>"

// chequeo primer caracter
if (header.charAt(0) !== "<") {
    throw new Error(`mensaje malformado: se esperaba un "<", se obtuvo un: "${header.charAt(0)}"`);
}

// chequeo ultimo caracter
if (header.charAt(6) !== ">") {
    throw new Error(`mensaje malformado: se esperaba un ">", se obtuvo un: "${header.charAt(6)}"`);
}

// chequeo que la facility exista
idFacility = parseInt(header.substring(1, 6));
idFacility = idFacility < 20000 ? idFacility : idFacility - 10000;
const facilities = global.get("facilities") || {};
if (!facilities[idFacility]) {
    throw new Error(`no existe esta facility: ${idFacility}, ${header}`);
}

// Function to extract k bits from p position
// and returns the extracted value as integer
const bitExtracted = (number, k, p) => (((1 << k) - 1) & (number >> p));

if (isMM) {
    // chequeo primera "query param"
    let buffer = msg.payload.subarray(7, 11);
    let substring = buffer.toString('utf-8');
    if (substring !== "nro=") {
        throw new Error(`mensaje malformado: se esperaba un "nro=", se obtuvo un: "${substring}"`);
    }

    // chequeo segunda "query param"
    buffer = msg.payload.subarray(14, 20);
    substring = buffer.toString('utf-8');
    if (substring !== "&data=") {
        throw new Error(`mensaje malformado: se esperaba un "&data=", se obtuvo un: "${substring}"`);
    }

    let mqttBuffer = global.get("mqttBuffer") || {};
    if (!mqttBuffer[idFacility]) {
        mqttBuffer = {
            ...mqttBuffer,
            [idFacility]: {
                metadata: facilities[idFacility]
            }
        };
    }
    global.set("mqttBuffer", mqttBuffer);

    const metadata = mqttBuffer[idFacility].metadata

    /*
        "mens-mil": {
            nro_mm: 2,                          // word nro
            clasificacion: "reservado",         // bits 0 y 1
            precedencia: "rutina",              // bits 2 y 3
            destino: "Puesto Comando",          // bits 4, 5 y 6
            origen: "Centro de Mensajes",       // bits 0, 1 y 2
            evento: "entregar",                 // bits 3 y 4
            cifrado: true,                      // bit 5
        },
    */

    buffer = msg.payload.subarray(11, 13);
    const nro_mm = buffer[0] * 256 + buffer[1];

    buffer = msg.payload.subarray(20, 22);
    const clasificacion = bitExtracted(buffer[0], 2, 0) + 1;
    const precedencia = bitExtracted(buffer[0], 2, 2) + 1;;
    const origen = bitExtracted(buffer[0], 3, 4) + 1;
    const destino = bitExtracted(buffer[1], 3, 0) + 1;
    const evento = bitExtracted(buffer[1], 2, 3) + 1;
    const cifrado = bitExtracted(buffer[1], 1, 5);

    const loraData = {
        nro_mm,
        clasificacion,
        precedencia,
        destino,
        origen,
        evento,
        cifrado,
    };


    msg.payload = {
        metadata,
        loraData
    }

} else {
    const loraPayload = msg.payload.substring(msg.payload.indexOf(">") + 1);
    let loraData = {};
    const validLoraKeys = global.get("validLoraKeys");
    // ToDo: aÃ±adir mensajes militares
    loraPayload.split("&").forEach(str => {
        const arr = str.split("=");
        if (!validLoraKeys.includes(arr[0])) {
            throw new Error(`entry invalido en carga util de LoRa: ${arr[0]}`);
        }
        loraData = {
            ...loraData,
            [arr[0]]: arr[1]
        };
    });


    let mqttBuffer = global.get("mqttBuffer") || {};
    if (!mqttBuffer[idFacility]) {
        mqttBuffer = {
            ...mqttBuffer,
            [idFacility]: {
                metadata: facilities[idFacility]
            }
        };
    }

    mqttBuffer[idFacility].loraData = {
        ...mqttBuffer[idFacility].loraData,
        ...loraData
    };

    global.set("mqttBuffer", mqttBuffer);
    msg.payload = mqttBuffer[idFacility];
}

return msg;

