const horusAPI = global.get("horusAPI")
const diccionario = horusAPI.diccionario;
const traduccionEstados = {
    S: "en servicio",
    L: "servicio limitado",
    F: "fuera de servicio"
}

const requests = [];

const naftaDebug = horusAPI.uiNaftaDebugMode;
const naftaPercentageDebug = horusAPI.uiNaftaDebugPercentage;
const gpsDebug = horusAPI.uiGPSDebugMode;
const latDebug = horusAPI.uiGPSDebugLat;
const longDebug = horusAPI.uiGPSDebugLong;
const altDebug = horusAPI.uiGPSDebugAlt;
const timestamp = parseInt(new Date().getTime() / 1000);
const facilidad = msg.payload.metadata.name;
const generador = msg.payload.metadata.generator;
const estado = traduccionEstados[msg.payload.loraData.status] || "fuera de servicio"

if (!msg.payload.loraData.nro_mm) {
    requests.push({
        endpoint: "estado-servicio",
        type: "metrica",
        body: {
            facilidad,
            estado,
            timestamp
        }
    });

    if (msg.payload.loraData.voltage || msg.payload.loraData.current) {
        requests.push({
            endpoint: "alimentacion",
            type: "metrica",
            body: {
                generador,
                tension: parseFloat(msg.payload.loraData.voltage) || 0.0,
                corriente: parseFloat(msg.payload.loraData.current) || 0.0,
                timestamp
            }
        });
    }

    if (msg.payload.loraData.temperature) {
        requests.push({
            endpoint: "temperatura",
            type: "evento",
            body: {
                facilidad,
                temperature: parseFloat(msg.payload.loraData.temperature) || 0.0,
                timestamp
            }
        });
    }

    if (msg.payload.loraData.gas) {
        let nivel = parseFloat(msg.payload.loraData.gas?.split("/")[0]) || 0.0;
        const capacidad = parseFloat(msg.payload.loraData.gas?.split("/")[1]) || 1.0;
        if (naftaDebug) {
            nivel = capacidad * naftaPercentageDebug / 100.0; 
        }
        requests.push({
            endpoint: "nivel-combus",
            type: "metrica",
            body: {
                generador,
                nivel,
                capacidad,
                timestamp
            }
        });
    }

    if (msg.payload.loraData.lat) {
        if (gpsDebug) {
            requests.push({
                endpoint: "geolocalizacion",
                type: "evento",
                body: {
                    facilidad,
                    latitude: latDebug,
                    longitude: longDebug,
                    altitude: altDebug,
                    timestamp,
                }
            });
        } else {
            if (msg.payload.loraData.lat !== "***") {
                requests.push({
                    endpoint: "geolocalizacion",
                    type: "evento",
                    body: {
                        facilidad,
                        latitude: parseFloat(msg.payload.loraData.lat) || 0.0,
                        longitude: parseFloat(msg.payload.loraData.lng) || 0.0,
                        altitude: parseFloat(msg.payload.loraData.alt) || 0.0,
                        timestamp,
                    }
                });
            }
        }       
    }

    requests.push({
        endpoint: "sensor_bool",
        type: "evento",
        body: {
            facilidad,
            evento: "rain",
            valor: msg.payload.loraData.raindrops === "1",
            timestamp
        }
    });
} else {

    const nro_mm = msg.payload.loraData.nro_mm;
    const clasificacion = diccionario.clasificaciones[msg.payload.loraData.clasificacion];
    const precedencia = diccionario.precedencias[msg.payload.loraData.precedencia];
    const cifrado = msg.payload.loraData.cifrado === 1;
    const destino = diccionario.redes[msg.payload.loraData.destino];
    const origen = diccionario.redes[msg.payload.loraData.origen];
    const evento = diccionario.eventos[msg.payload.loraData.evento];

    requests.push({
        endpoint: "mens-mil",
        type: "evento",
        body: {
            nro_mm,
            clasificacion,
            precedencia,
            cifrado,
            destino,
            origen,
            evento,
            timestamp
        }
    })
}

msg.payload = requests;
return msg;

const { validLoraKeys, sensorica } = global.get("sigefaAPI");

const loraPayload = msg.payload.substring(msg.payload.indexOf(":") + 1);
let loraData = {};

loraPayload.replace(/(\r\n|\n|\r)/gm, "").split(" ").forEach(str => {
    if (str.length > 0) {
        const arr = str.replace(",", "").split("=");
        if (!validLoraKeys.includes(arr[0])) {
            throw new Error(`entry invalido en carga util de LoRa: ${arr[0]}`);
