let horusAPI = global.get("horusAPI");
const loraId = 10009;
const fuelLimit = 150;

const rng = (min, max) => (Math.random() * (max - min + 1) + min).toFixed(2);
const rngB = () => Math.random() > 0.5 ? 1 : 0;
const nroMM = () => {
    const nro = horusAPI.nroMM || 1;
    horusAPI = {
        ...horusAPI,
        nroMM: nro + 1
    }
    global.set("horusAPI", horusAPI);
    return nro;
}
const trunc = (str) => parseFloat(str.charAt(0))
const possibleStatus = ['S', 'L', 'F']

const generarPayloadInt = () => `<${loraId}>voltage=${rng(210, 230)}&temperature=${rng(5, 30)}&status=${possibleStatus[trunc(rng(0, 2))]}`;
const generarPayloadExt = () => `<${10000 + loraId}>current=${rng(0.5, 10)}&raindrops=${rngB()}&gas=${rng(0, fuelLimit)}/${fuelLimit}&lat=${-34.57475 + parseFloat(rng(-0.3, 0.3))}&lng=${-58.43552 + parseFloat(rng(-4.0, 0.0))}&alt=15`;

/* 
    nro_mm: 2,
    clasificacion: "reservado",
    precedencia: "rutina",
    cifrado: true,
    destino: "Puesto Comando",
    origen: "Centro de Mensajes",
    evento: "entregar",
*/
const generarMM = () => {
    const clasificacion = trunc(rng(1, 4));             // 1: publico, 2: reservado, 3: confidencial, 4: secreto
    const numeroMsj = nroMM();                              
    const precedencia = trunc(rng(1, 4));               // 1: rutina, 2: prioridad, 3: inmediato, 4: flash
    const cifrado = clasificacion >= 3 ? rngB() : 0;      
    const indexOrigen = trunc(rng(1, 5));               // 1: Red Cdo Op, 2: Red Mat Pers, 3: Red Icia, 4: Red Cdo, 5: Red Op
    const indexDestino = indexOrigen % 5 + 1;           // 1: Red Cdo Op, 2: Red Mat Pers, 3: Red Icia, 4: Red Cdo, 5: Red Op
    const evento = trunc(rng(1, 4));                    // 1: generado, 2: recibido_estafeta, 3: entregado_estafeta, 4: transmitido 

    const nro = numeroMsj % 65536;

    const nroArr = [0, 0, 0];
    nroArr[0] = (nro - nro % 256) / 256;
    nroArr[1] = nro % 256;
    nroArr[2] = 0;

    const dataArr = [0, 0, 0];
    dataArr[0] += (clasificacion - 1) * 2**0;    // bits 0 y 1
    dataArr[0] += (precedencia - 1) * 2**2;      // bits 2 y 3
    dataArr[0] += (indexOrigen - 1) * 2**4;      // bits 4, 5 y 6
    dataArr[1] += (indexDestino - 1) * 2**0;     // bits 0, 1 y 2
    dataArr[1] += (evento - 1) * 2**3;           // bits 3 y 4
    dataArr[1] += cifrado * 2**5;                // bit 5


    // debug (no es outputteable a mqtt)
    msg.metadata = {
        clasificacion,
        numeroMsj,
        precedencia,
        cifrado,
        indexOrigen,
        indexDestino, 
        evento,
        bytesNumeroMsj: `${nroArr[0]} ${nroArr[1]} ${nroArr[2]}`,
        bytesData: `${dataArr[0]} ${dataArr[1]} ${dataArr[2]}`,
    };

    return Buffer.from(`<${loraId}>nro=${String.fromCharCode(...nroArr)}&data=${String.fromCharCode(...dataArr)}`);
}

if (msg.payload === "nodo interior") {
    msg.payload = generarPayloadInt();
} else if (msg.payload === "nodo exterior") {
    msg.payload = generarPayloadExt();
} else if (msg.payload === "mensaje militar") {
    msg.payload = generarMM();
}

return msg;

const loRaId = global.get("horusAPI").uiLoRaId;
const automaticMode = global.get("horusAPI").uiAutomaticMode;
let loRaCmd = "";

const checkDayTime = () => {
    const horaDiurna = 7;
    const horaNocturna = 19;
    const horaActual = new Date().getHours();
    return horaActual >= horaDiurna && horaActual < horaNocturna;
}

if (msg.payload == "start alert") {
    loRaCmd = "startAlert";
} else {
    if (automaticMode) {
        if (msg.payload == "auto loop") {
            if (checkDayTime()) {
                loRaCmd = "daytime";
            } else {
                loRaCmd = "nighttime";
            }
        }
    } else {
        if (msg.payload == "manual day time") {
            loRaCmd = "daytime";
        } else if (msg.payload == "manual night time") {
            loRaCmd = "nighttime";
        } 
    }
}

if (loRaCmd) {
    msg.payload = `<${loRaId}>${loRaCmd}`;
    return msg;
} 

return;

