const fuelLimit = 12;

const rng = (min, max) => (Math.random() * (max - min + 1) + min).toFixed(2);
const rngB = () => Math.random() > 0.5 ? 1 : 0;
const nroMM = () => {
    const nro = horusAPI.nroMM || 1 % 65536;
    horusAPI = {
        ...horusAPI,
        nroMM: nro + 1
    }
    global.set("horusAPI", horusAPI);
