}
const trunc = (str) => parseFloat(str.charAt(0))
const possibleStatus = ['S', 'L', 'F']

const generarPayloadInt = () => `<${loraId}>voltage=${rng(210, 230)}&temperature=${rng(5, 30)}&status=${possibleStatus[trunc(rng(0, 2))]}`;
const generarPayloadExt = () => `<${10000 + loraId}>current=${rng(0.5, 10)}&raindrops=${rngB()}&gas=${rng(0, fuelLimit)}/${fuelLimit}&lat=${-34.57475 + parseFloat(rng(-0.3, 0.3))}&lng=${-58.43552 + parseFloat(rng(-4.0, 0.0))}&alt=15`;

const reducer = {
    "nro_mm": "nro",
    "origen": "o",
    "destino": "d",
    "clasificacion": "cl",
    "precedencia": "p",
    "cifrado": "ci",
    "evento": "e",
