}

const stringify = (obj) => {
    const keys = Object.keys(obj);
    let str = `<${loraId}>`;
    keys.forEach((key, index) => {
        const isLast = keys.length === index + 1;
        str += reducer[key];
        str += "="
        str += obj[key];
        if (!isLast) {
            str += "&";
        }
    });
    return str;
}

/* 
    nro_mm: 2,
    clasificacion: "reservado",
    precedencia: "rutina",
    cifrado: true,
    destino: "Puesto Comando",
    origen: "Centro de Mensajes",
    evento: "entregar",
*/
const generarMM = () => {
    const clasificacion = trunc(rng(1, 4));             // 1: publico, 2: reservado, 3: confidencial, 4: secreto
    const nro_mm = nroMM();                              
    const precedencia = trunc(rng(1, 4));               // 1: rutina, 2: prioridad, 3: inmediato, 4: flash
    const cifrado = clasificacion >= 3 ? String(rngB()) : "0";      
    const origen = trunc(rng(1, 5));               // 1: Red Cdo Op, 2: Red Mat Pers, 3: Red Icia, 4: Red Cdo, 5: Red Op
    const destino = origen % 5 + 1;           // 1: Red Cdo Op, 2: Red Mat Pers, 3: Red Icia, 4: Red Cdo, 5: Red Op
    const evento = trunc(rng(1, 4));                    // 1: generado, 2: recibido_estafeta, 3: entregado_estafeta, 4: transmitido 
    const mensaje = "test";


    // debug (no es outputteable a mqtt)
    msg.metadata = {
        nro_mm,
        origen,
        destino,
        clasificacion,
        precedencia,
        cifrado,
        evento,
        mensaje
    };

    return stringify(msg.metadata);
}

if (msg.payload === "nodo interior") {
