/* 
    mensaje de ejemplo
    ORIGEN: Red Mat Pers
    CLASIFICACION: publico
    PRECEDENCIA: rutina
    EVENTO: generado
    MENSAJE: xxx
*/

const searchInDescription = (key, arr) => {
    const found = arr.find(element => element.includes(key.toUpperCase()));
    if (!found) {
        if (key === "Cifrado") {
            return 0;
        }
        throw new Error(`no se pudo obtener el ${key} del mensaje`);
    }
    if (key === "Cifrado") {
        return 1;
    }

    const payload = found.split(":")[1].trim();
    if (key === "Origen" || key === "Destino") {
        const traducido = diccionario.redes[payload];
        if (!traducido) {
            throw new Error(`no se encontro ${payload} en el diccionario`);
        }
        return traducido;
    } else if (key === "Clasificacion") {
        const traducido = diccionario.clasificaciones[payload];
        if (!traducido) {
            throw new Error(`no se encontro ${payload} en el diccionario`);
        }
        return traducido;
    } else if (key === "Precedencia") {
        const traducido = diccionario.precedencias[payload];
        if (!traducido) {
            throw new Error(`no se encontro ${payload} en el diccionario`);
        }
        return traducido;
    } else if (key === "Evento") {
        const traducido = diccionario.eventos[payload];
        if (!traducido) {
            throw new Error(`no se encontro ${payload} en el diccionario`);
        }
        return traducido;
    } else if (key === "Mensaje") {
        return payload;
    }
}

const traductorSIGEFAHorus = (sigefaObj) => {
    const { id, subtipo_id, description } = sigefaObj;
    const descriptionArrayByLines = description.split("\n");
    let destino, origen;
    if (subtipo_id === 1) {
        // mensaje entrante
        destino = facilidadId;
        origen = searchInDescription("Origen", descriptionArrayByLines);
    } else if (subtipo_id === 2) {
        // mensaje saliente
        origen = facilidadId;
        destino = searchInDescription("Destino", descriptionArrayByLines);
    } else {
        throw new Error("subtipo de mensaje SIGEFA desconocido");
    }
    const clasificacion = searchInDescription("Clasificacion", descriptionArrayByLines);
    const precedencia = searchInDescription("Precedencia", descriptionArrayByLines);
    const cifrado = searchInDescription("Cifrado", descriptionArrayByLines);
    const evento = searchInDescription("Evento", descriptionArrayByLines);
    const mensaje = searchInDescription("Mensaje", descriptionArrayByLines);
    return {
        nro_mm: id,
        origen, 
        destino,
        clasificacion,
        precedencia,
        cifrado,
        evento,
        mensaje
    }
};

msg.payload = traductorSIGEFAHorus(lastObject);

return msg;

