}

const stringify = (obj) => {
    const keys = Object.keys(obj);
    let str = "USB: ";
    keys.forEach((key, index) => {
        const isLast = keys.length === index + 1;
        str += reducer[key];
        str += "="
        str += obj[key];
        if (!isLast) {
            str += "&";
        }
    });
    return str;
}

