return msg;

let horusAPI = global.get("horusAPI") || {};
const token = msg.payload.simulador || msg.payload.token;

if (token.length !== 32) {
    throw new Error ("token invalido Horus API");
}

const headers = {
    Authorization: `Bearer ${token}`
}

horusAPI = {
    ...horusAPI,
    headers
};
global.set("horusAPI", horusAPI);

msg.payload = headers;
return msg;

const isMM = typeof msg.payload !== "string";
let idFacility;
let header = "";
let horusAPI = global.get("horusAPI") || {};
if (isMM) {
    const buffer = msg.payload.subarray(0, 7);
    header = buffer.toString('utf-8');
} else {
    header = msg.payload.substring(0, 7);
}
// p. ej.: header = "<10009>"

// chequeo primer caracter
if (header.charAt(0) !== "<") {
    throw new Error(`mensaje malformado: se esperaba un "<", se obtuvo un: "${header.charAt(0)}"`);
}

// chequeo ultimo caracter
if (header.charAt(6) !== ">") {
    throw new Error(`mensaje malformado: se esperaba un ">", se obtuvo un: "${header.charAt(6)}"`);
}

// chequeo que la facility exista
idFacility = parseInt(header.substring(1, 6));
idFacility = idFacility < 20000 ? idFacility : idFacility - 10000;
const facilities = horusAPI.facilities || {};
if (!facilities[idFacility]) {
    throw new Error(`no existe esta facility: ${idFacility}, ${header}`);
}

// Function to extract k bits from p position
// and returns the extracted value as integer
const bitExtracted = (number, k, p) => (((1 << k) - 1) & (number >> p));
let mqttBuffer = {};
if (isMM) {
    // chequeo primera "query param"
    let buffer = msg.payload.subarray(7, 11);
    let substring = buffer.toString('utf-8');
    if (substring !== "nro=") {
        throw new Error(`mensaje malformado: se esperaba un "nro=", se obtuvo un: "${substring}"`);
    }

    // chequeo segunda "query param"
    buffer = msg.payload.subarray(14, 20);
    substring = buffer.toString('utf-8');
    if (substring !== "&data=") {
        throw new Error(`mensaje malformado: se esperaba un "&data=", se obtuvo un: "${substring}"`);
    }

    mqttBuffer = horusAPI.mqttBuffer || {};
    if (!mqttBuffer[idFacility]) {
        mqttBuffer = {
            ...mqttBuffer,
            [idFacility]: {
                metadata: facilities[idFacility]
            }
        };
    }

    const metadata = mqttBuffer[idFacility].metadata

    /*
        "mens-mil": {
            nro_mm: 2,                          // word nro
            clasificacion: "reservado",         // bits 0 y 1
            precedencia: "rutina",              // bits 2 y 3
            destino: "Puesto Comando",          // bits 4, 5 y 6
            origen: "Centro de Mensajes",       // bits 0, 1 y 2
            evento: "entregar",                 // bits 3 y 4
            cifrado: true,                      // bit 5
        },
    */

    buffer = msg.payload.subarray(11, 13);
    const nro_mm = buffer[0] * 256 + buffer[1];

    buffer = msg.payload.subarray(20, 22);
    const clasificacion = bitExtracted(buffer[0], 2, 0) + 1;
    const precedencia = bitExtracted(buffer[0], 2, 2) + 1;;
    const origen = bitExtracted(buffer[0], 3, 4) + 1;
    const destino = bitExtracted(buffer[1], 3, 0) + 1;
    const evento = bitExtracted(buffer[1], 2, 3) + 1;
    const cifrado = bitExtracted(buffer[1], 1, 5);

    const loraData = {
        nro_mm,
        clasificacion,
        precedencia,
        destino,
        origen,
        evento,
        cifrado,
    };


    msg.payload = {
        metadata,
        loraData
    }

} else {
    const loraPayload = msg.payload.substring(msg.payload.indexOf(">") + 1);
    let loraData = {};
    const validLoraKeys = horusAPI.validLoraKeys;
    // ToDo: aÃ±adir mensajes militares
    loraPayload.split("&").forEach(str => {
        const arr = str.split("=");
        if (!validLoraKeys.includes(arr[0])) {
            throw new Error(`entry invalido en carga util de LoRa: ${arr[0]}`);
        }
        loraData = {
            ...loraData,
            [arr[0]]: arr[1]
        };
    });


    mqttBuffer = horusAPI.mqttBuffer || {};
    if (!mqttBuffer[idFacility]) {
        mqttBuffer = {
            ...mqttBuffer,
            [idFacility]: {
                metadata: facilities[idFacility]
            }
        };
    }

    mqttBuffer[idFacility].loraData = {
        ...mqttBuffer[idFacility].loraData,
        ...loraData
    };

    msg.payload = mqttBuffer[idFacility];
}

horusAPI = {
    ...horusAPI,
    mqttBuffer
};
global.set("horusAPI", horusAPI);

return msg;

const config = global.get("horusAPI").config;
const headers = global.get("horusAPI").headers;
const baseUrl = msg.payload.type === "metrica" ? config.url_metricas : config.url_eventos;

msg = {
    payload: JSON.stringify(msg.payload.body),
    url: baseUrl + msg.payload.endpoint,
    headers
}

return msg;

const diccionario = global.get("horusAPI").diccionario;
const traduccionEstados = {
    S: "en servicio",
    L: "servicio limitado",
    F: "fuera de servicio"
}

const requests = [];

const timestamp = parseInt(new Date().getTime() / 1000);
const facilidad = msg.payload.metadata.name;
const generador = msg.payload.metadata.generator;
const estado = traduccionEstados[msg.payload.loraData.status] || "fuera de servicio"

if (!msg.payload.loraData.nro_mm) {
    requests.push({
        endpoint: "estado-servicio",
        type: "metrica",
        body: {
            facilidad,
            estado,
            timestamp
        }
    });

    if (msg.payload.loraData.voltage || msg.payload.loraData.current) {
        requests.push({
            endpoint: "alimentacion",
            type: "metrica",
            body: {
                generador,
                tension: parseFloat(msg.payload.loraData.voltage) || 0.0,
                corriente: parseFloat(msg.payload.loraData.current) || 0.0,
                timestamp
            }
        });
    }

    if (msg.payload.loraData.temperature) {
        requests.push({
            endpoint: "temperatura",
            type: "evento",
            body: {
                facilidad,
                temperature: parseFloat(msg.payload.loraData.temperature) || 0.0,
                timestamp
            }
        });
    }

    if (msg.payload.loraData.gas) {
        const nivel = parseFloat(msg.payload.loraData.gas?.split("/")[0]) || 0.0;
        const capacidad = parseFloat(msg.payload.loraData.gas?.split("/")[1]) || 1.0;
        requests.push({
            endpoint: "nivel-combus",
            type: "metrica",
            body: {
                generador,
                nivel,
                capacidad,
                timestamp
            }
        });
    }

    if (msg.payload.loraData.lat && msg.payload.loraData.lat !== "***") {
        requests.push({
            endpoint: "geolocalizacion",
            type: "evento",
            body: {
                facilidad,
                latitude: parseFloat(msg.payload.loraData.lat) || 0.0,
                longitude: parseFloat(msg.payload.loraData.lng) || 0.0,
                altitude: parseFloat(msg.payload.loraData.alt) || 0.0,
                timestamp,
            }
        });
    }

    requests.push({
        endpoint: "sensor_bool",
        type: "evento",
        body: {
            facilidad,
            evento: "rain",
            valor: msg.payload.loraData.raindrops === "1",
            timestamp
        }
    });
} else {

    const nro_mm = msg.payload.loraData.nro_mm;
    const clasificacion = diccionario.clasificaciones[msg.payload.loraData.clasificacion];
    const precedencia = diccionario.precedencias[msg.payload.loraData.precedencia];
    const cifrado = msg.payload.loraData.cifrado === 1;
    const destino = diccionario.redes[msg.payload.loraData.destino];
    const origen = diccionario.redes[msg.payload.loraData.origen];
    const evento = diccionario.eventos[msg.payload.loraData.evento];

    requests.push({
        endpoint: "mens-mil",
        type: "evento",
        body: {
            nro_mm,
            clasificacion,
            precedencia,
            cifrado,
            destino,
            origen,
            evento,
            timestamp
        }
    })
}

msg.payload = requests;
return msg;

const { validLoraKeys, sensorica } = global.get("sigefaAPI");

const loraPayload = msg.payload.substring(msg.payload.indexOf(":") + 1);
let loraData = {};

loraPayload.replace(/(\r\n|\n|\r)/gm, "").split(" ").forEach(str => {
    if (str.length > 0) {
        const arr = str.replace(",", "").split("=");
        if (!validLoraKeys.includes(arr[0])) {
            throw new Error(`entry invalido en carga util de LoRa: ${arr[0]}`);
        }
        if (arr[0] in sensorica) {
            loraData = {
                ...loraData,
                [arr[0]]: arr[1]
            };
        }
        if (arr[0] === "emergency") {
            loraData = {
                ...loraData,
                emergency: arr[1]
            }
        } 
    }

});


msg.payload = loraData;


return msg;

const { sensorica, baseUrl, facilidadId, traduccion } = global.get("sigefaAPI");
const previousStatus = global.get("sigefaAPI").status;
let body = {};
let url = "";

const isSensor = msg.key in sensorica;

if (isSensor) {
    const id = parseInt(sensorica[msg.key]);
    url = `${baseUrl}/api/facilidad/${facilidadId}/sensor/${id}/edit/`;
    const nombre = traduccion[msg.key];
    const indicador = msg.key === "gas" ? parseFloat(msg.payload.split("/")[0]) : parseFloat(msg.payload);
    body = {
        id,
        nombre,
        indicador
    };
} else { // it should be an emergency!
    if (msg.key !== "emergency") {
        throw new Error(`error procesando esto: ${msg.key}`)
    }
    url = `${baseUrl}/api/facilidad/${facilidadId}/status`;
    const status = msg.payload === "1" ? "F" : previousStatus;
    body = {
        nombre: "Red Cdo",
        status
    }
}

msg = {
    payload: body,
    url
}

return msg;

const { baseUrl, facilidadId } = global.get("sigefaAPI");
const url = `${baseUrl}/api/facilidad/${facilidadId}/status`;

msg.url = url;

return msg;

let sigefaAPI = global.get("sigefaAPI");

const status = msg.payload.status;
if (!["F", "S", "L"].includes(status)) {
    throw new Error ("status desconocido: " + status);
}

if (sigefaAPI.firstRun || !sigefaAPI.status || status !== sigefaAPI.status) {
    msg.payload = `USB: status=${status}\n`;
    sigefaAPI = {
        ...sigefaAPI,
        firstRun: false,
        status
    }
    global.set("sigefaAPI", sigefaAPI);
    return msg;
}

return;

